section.programlisting:
#!/bin/sh
# pass all arguments to cmvn with $@
exec java -jar cmvn-executable-0.2.0.jar "$@"

:init @REM Decide how to startup depending on the version of windows
@REM -- Windows NT with Novell Login if "%OS%"=="WINNT" goto WinNTNovell
@REM -- Win98ME if NOT "%OS%"=="Windows_NT" goto Win9xArg
:WinNTNovell
@REM -- 4NT shell if "%@eval[2+2]" == "4" goto 4NTArgs
@REM -- Regular WinNT shell set CMVN_CMD_LINE_ARGS=%* goto endInit
@REM The 4NT Shell from jp software :4NTArgs set CMVN_CMD_LINE_ARGS=%$ goto endInit
:Win9xArg @REM Slurp the command line arguments. This loop allows for an unlimited number @REM of agruments (up to the command line limit, anyway). set CMVN_CMD_LINE_ARGS= :Win9xApp if %1a==a goto endInit set CMVN_CMD_LINE_ARGS=%CMVN_CMD_LINE_ARGS% %1 shift goto Win9xApp
@REM Reaching here means variables are defined and arguments have been captured :endInit SET CMVN_JAVA_EXE="%JAVA_HOME%\bin\java.exe"
%CMVN_JAVA_EXE% -jar cmvn-executable.jar %CMVN_CMD_LINE_ARGS%
set CMVN_JAVA_EXE= set CMVN_CMD_LINE_ARGS=
